import { Component} from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { FormControl, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-popup',
  templateUrl: './popup.component.html',
  styleUrls: ['./popup.component.css']
})
export class PopupComponent {
  constructor(
    private http: HttpClient,
    private dialog: MatDialog
  ){}

  stock:String=""
  brand:String=""
  price:String=""
  itemName:String=""
  dateEntered:String=""

  stockc= new FormControl('',[
    Validators.required
  ])
  brandc= new FormControl('',[
    Validators.required
  ])

  pricec= new FormControl('',[
    Validators.required
  ])

  itemNamec= new FormControl('',[
    Validators.required
  ])

  dateEnteredc= new FormControl('',[
    Validators.required
  ])

  enterProduct(){
    let bodydata={
      "stock":this.stock,
      "brand":this.brand,
      "price":this.price,
      "itemName":this.itemName,
      "dataEntered":this.dateEntered
    }
    this.http.post("http://localhost:8000/products/createdata",bodydata).subscribe((resultData:any)=>{
      console.log(resultData)
      alert("Product is Entered")
    })

  }
  onSubmit(){
    this.enterProduct()
  }
  onClose(){
    this.dialog.closeAll()
  }
}
<div class="main">
    <div class="form1">
        <div class="form2">
            <form class="form">
                <div class="set1">
                    <div> 
                        <input [(ngModel)]="itemName" [formControl]="itemNamec" type="text" placeholder="Item name"  class="inp" />
                    </div>
                    <div>

                        <input [(ngModel)]="brand" [formControl]="brandc" type="text" placeholder="Brand"  class="inp" />

                    </div>
                    <div>

                        <input [(ngModel)]="stock" [formControl]="stockc" type="datetime" placeholder="Stock" class="inp" />

                    </div>
                </div>
                <div class="set2">
                    <div>

                        <input [(ngModel)]="price" [formControl]="pricec" type="text" placeholder="Price" class="inp"/>
                    </div>
                    <div>

                        <input [(ngModel)]="dateEntered" [formControl]="dateEnteredc" type="text" placeholder="Date Entered" class="inp"/>
                    </div>
                    <div class="btn">
                        <button type="submit"  class="btnsubmit" (click)="onSubmit()">Submit</button>
                        <button type="submit"  class="btnsubmit2" (click)="onClose()">Close</button>
                    </div>
                </div>

            </form>
        </div>

    </div>
</div>
